mapper.select:
select *
  from vote v join vote_question q 
    using(vote_no) 
    join tb_member m on v.member_id =m.id 
where vote_no = #{voteNo}

 select *
  from vote v join vote_question q 
    using(vote_no) 
    join tb_member m on v.member_id =m.id 
where vote_no = #{voteNo}

    select *
  from vote v join vote_question q 
    using(vote_no) 
    join tb_member m on v.member_id =m.id 
where group_no = #{groupNo} and vote_no != #{voteNo}
	order by v.vote_enroll_date desc
